import React, { useContext } from 'react';
import { NavLink } from 'react-router-dom';
import styled from 'styled-components';
import { ThemeContext } from '../context/ThemeContext';
import { motion } from 'framer-motion';

const Nav = styled.nav`
  position: fixed; top: 0; width: 100%; display: flex;
  justify-content: space-between; align-items: center;
  padding: 1rem 2rem; background: ${({ theme }) => theme.body};
  box-shadow: 0 2px 6px rgba(0,0,0,0.1); z-index: 100;
`;

const Menu = styled.div`
  display: flex; gap: 1.5rem;
  a { font-weight: 500; }
`;

const Toggle = styled.button`
  background: none; border: none; cursor: pointer;
  font-size: 1.2rem; color: ${({ theme }) => theme.text};
`;

export default function Navbar() {
  const { theme, toggleTheme } = useContext(ThemeContext);
  return (
    <Nav>
      <motion.h1
        initial={{ opacity: 0 }} animate={{ opacity: 1 }}
        transition={{ duration: 0.5 }}
      >
        Stroeykens
      </motion.h1>
      <Menu>
        {['/', '/career', '/highlights', '/contact'].map(path => (
          <NavLink key={path} to={path}>
            {path === '/' ? 'Home' : path.slice(1).charAt(0).toUpperCase() + path.slice(2)}
          </NavLink>
        ))}
      </Menu>
      <Toggle onClick={toggleTheme}>
        {theme === 'light' ? 'üåô' : '‚òÄÔ∏è'}
      </Toggle>
    </Nav>
  );
}
