import React from 'react';
import styled from 'styled-components';

const Section = styled.section`
  max-width: 800px; margin: 2rem auto; padding: 0 1rem;
`;

const Heading = styled.h2`
  color: ${({ theme }) => theme.accent}; font-size: 2rem; margin-bottom: 1rem;
`;

const List = styled.ul`
  list-style: none; padding: 0; li { margin-bottom: 1rem; }
`;

export default function Career() {
  return (
    <Section>
      <Heading>Career Highlights</Heading>
      <List>
        <li><strong>Debut:</strong> 15 Jan 2021 vs. Eupen (16y, 3m, 17d old)</li>
        <li><strong>First Goal:</strong> 9 Oct 2022 vs. KV Mechelen</li>
        <li><strong>League Stats:</strong> 97 apps, 12 goals (as of Mar 2025)</li>
        <li><strong>Contract:</strong> Until 30 June 2026</li>
        <li><strong>Positions:</strong> Attacking Midfielder / Left Wing / 2nd Striker</li>
        <li><strong>National:</strong> Belgium U15–U21 (12 U21 caps)</li>
      </List>

      <Heading>Achievements</Heading>
      <List>
        <li>Forbes Belgium 30 Under 30, Class of 2024</li>
        <li>Breakout Season 2023–24: 6G &amp; 6A in 40 matches</li>
        <li>Scored stunning long‑range goal vs. Standard Liège, Dec 2023</li>
      </List>
    </Section>
  );
}
