import React from 'react';
import styled from 'styled-components';
import insta from '../assets/instagram.svg';
import twitter from '../assets/twitter.svg';
import youtube from '../assets/youtube.svg';

const Section = styled.section`
  text-align: center; padding: 4rem 1rem;
`;

const IconBar = styled.div`
  display: flex; justify-content: center; gap: 2rem; margin-top: 1.5rem;
  img { width: 40px; cursor: pointer; }
`;

export default function Contact() {
  return (
    <Section>
      <h2 style={{ color: 'var(--accent)' }}>Connect With Me</h2>
      <p>Follow for updates and behind‑the‑scenes!</p>
      <IconBar>
        <a href="https://www.instagram.com/m.stroeykens/" target="_blank" rel="noopener noreferrer">
          <img src={insta} alt="Instagram" />
        </a>
        <a href="https://twitter.com/MarioStroeykens" target="_blank" rel="noopener noreferrer">
          <img src={twitter} alt="Twitter" />
        </a>
        <a href="https://www.youtube.com/c/ForbesBelgium" target="_blank" rel="noopener noreferrer">
          <img src={youtube} alt="YouTube" />
        </a>
      </IconBar>
    </Section>
  );
}
